const http = require('http');

const server = http.createServer((req,res)=>{
    if(req.url === '/'){
        res.end('Welcome to our home  page')
    }
if(req.url === '/about'){
    res.end('Here is our short History' )
}
res.end(
    `<h1>Ooops!!</h1>
   <p>Sorry Eric We are not finding what you looked for</p> 
   <a href="/">Back Home </a>
    `)
})

server.listen(5000)